	if err = randomize.Struct(seed, &b, roleMethodDBTypes, false, roleMethodColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}
	if err = randomize.Struct(seed, &c, roleMethodDBTypes, false, roleMethodColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}

	b.MethodID = a.ID
	c.MethodID = a.ID

	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	check, err := a.RoleMethods().All(ctx, tx)
	if err != nil {
		t.Fatal(err)
	}

	bFound, cFound := false, false
	for _, v := range check {
		if v.MethodID == b.MethodID {
			bFound = true
		}
		if v.MethodID == c.MethodID {
			cFound = true
		}
	}

	if !bFound {
		t.Error("expected to find b")
	}
	if !cFound {
		t.Error("expected to find c")
	}

	slice := MethodSlice{&a}
	if err = a.L.LoadRoleMethods(ctx, tx, false, (*[]*Method)(&slice), nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.RoleMethods); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	a.R.RoleMethods = nil
	if err = a.L.LoadRoleMethods(ctx, tx, true, &a, nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.RoleMethods); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	if t.Failed() {
		t.Logf("%#v", check)
	}
