func testMethodToManyAddOpRoleMethods(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Method
	var b, c, d, e RoleMethod

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, methodDBTypes, false, strmangle.SetComplement(methodPrimaryKeyColumns, methodColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*RoleMethod{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, roleMethodDBTypes, false, strmangle.SetComplement(roleMethodPrimaryKeyColumns, roleMethodColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*RoleMethod{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddRoleMethods(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.MethodID {
			t.Error("foreign key was wrong value", a.ID, first.MethodID)
		}
		if a.ID != second.MethodID {
			t.Error("foreign key was wrong value", a.ID, second.MethodID)
		}

		if first.R.Method != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Method != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.RoleMethods[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.RoleMethods[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.RoleMethods().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

